.feedback-button {
  position: fixed;
  display: flex;
  bottom: 50px;
  right: 30px;
  align-items: center;
  justify-content: center;
  background: url(../icons/contact/contact-background.svg);
  background-size: cover;
  height: $contact-background-size;
  width: $contact-background-size;
  z-index: 999;
  filter: drop-shadow(0px 4px 12px rgba(0, 0, 0, 0.25));

  &:hover {
    cursor: pointer;
  }

  &__icon-message {
    width: $contact-icon-size;
    height: $contact-icon-size;
    z-index: 999;
  }

  &__icons {
    position: absolute;
    bottom: $contact-icons-margin;
    display: flex;
    flex-direction: column;
    pointer-events: none;
    gap: 15px;
  }

  &__icon {
    opacity: 0;
    width: $contact-icon-size;
    height: $contact-icon-size;
    transition: all $transition-time;
    filter: drop-shadow(0px 4px 12px rgba(0, 0, 0, 0.25));

    &--telegram {
      transform: translateY($contact-icon-default-position);
    }

    &--viber {
      transform: translateY($contact-icon-default-position + 50px);
    }

    &--instagram {
      transform: translateY($contact-icon-default-position + 100px);
    }

    &--show {
      opacity: 1;
      transform: translateY(0);
      pointer-events: all;
    }
  }
}

